    public static Block block(String name, Block block)
    {
        Block fBlock = block.setRegistryName(Mubble.MOD_ID, name);
        BLOCKS.add(fBlock);
        return fBlock;
    }
    
    public static Block block(String name, Block block, ItemGroup group, List<Item> itemList)
    {
        Block fBlock = block.setRegistryName(Mubble.MOD_ID, name);
        BLOCKS.add(fBlock);
        if(itemList == FLOWERS || itemList == SAPLINGS) BLOCKS.add(new BlockFlowerPot(fBlock, Properties.from(Blocks.FLOWER_POT)).setRegistryName(Mubble.MOD_ID, "potted_" + name));
        itemList.add(new ItemBlock(fBlock, new Item.Properties().group(group)).setRegistryName(Mubble.MOD_ID, name));
        return fBlock;
    }
    
